import React from "react";
import Dropzone from "react-dropzone";

import CatalogueImageController, {
    Props
}
    from "./CatalogueImageController.web";

export const configJSON = require("./config");

export default class CatalogueImage extends CatalogueImageController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>
                <div className="admin-wrapper">
                    <div className="style-dropzone ">
                        <Dropzone onDrop={this.handleOnDrop} accept="image/*" minSize={0}
                            maxSize={5242880}
                            multiple>
                            {({ getRootProps, getInputProps }) => (
                                <div className="justify-center text-center" {...getRootProps()}>
                                    <input {...getInputProps()} />
                                    <i className="las la-cloud-upload-alt text-gray-300 la-lg" ></i>
                                    <p className="text-base">Drag Your Files To Upload</p>
                                    <p className="text-blue-600 text-sm">or click to the browser</p>
                                </div>
                            )}

                        </Dropzone>
                    </div>
                    <div>
                        <div >

                            {this.state.acceptedFiles.length > 0 && this.state.acceptedFiles.map((acceptedFile: any) => (
                                <span >
                                    <img src={URL.createObjectURL(acceptedFile)} alt="image" width={350} height={300}
                                        className="rounded-t-lg " />
                                </span>
                            ))}
                        </div>
                    </div>

                </div>
            </>

        );
    }
}
