import React from "react";

import { withRouter } from "react-router-dom";
import CatalogueController, { Props } from "./CatalogueController.web";
import { img } from "./assets";

class CatalogueRelated extends CatalogueController {
  constructor(props: Props) {
    super(props);
  }

  render() {
    return (
      <>
        {this.state.displayProducts.map((data: any, index: any) => (
          <div
            key={data.id}
            className="border-grey grid grid-cols-9 gap-4 px-8 py-4 rounded-lg mb-2 new-bg-color"
          >
            <div className="col-span-2 flex items-center">
              <label className="container-checkbox">
                <input
                  type="checkbox"
                  checked={data.checkboxStatus}
                  onChange={(e) => this.handleCheckBox(index, e.target.checked)}
                />
                <span className="checkmark" />
              </label>
              <img src={img} alt="" className="hw-46" />
              <div className=" pl-4">
                <p className="color-black font-bold">{data.name}</p>
                <p className="font-normal color-grey font-14">SKU Number</p>
              </div>
            </div>
            <div className="flex justify-center flex-col">
              <div className="chip w-auto text-center mr-2">
                {data.category}
              </div>
            </div>
            <div className="flex justify-center flex-col">
              <p className="color-black font-16">{data.qty}</p>
              <p className="font-normal color-grey font-14">Qty.</p>
            </div>
            <div className="col-span-3 flex items-center ">
              <div className="list-inner-divs">
                <p className="color-black font-16">
                  {data.currencySymbol}
                  {data.price.base}
                </p>
                <p className="font-normal color-grey font-14 details">Base</p>
              </div>
              <div className="list-inner-divs">
                <p className="color-black">
                  {data.currencySymbol}
                  {data.price.wholesale}
                </p>
                <p className="font-normal color-grey font-14 details">
                  Wholesale
                </p>
              </div>
              <div className="list-inner-divs">
                <p className="color-black">
                  {data.currencySymbol}
                  {data.price.highVol}
                </p>
                <p className="font-normal color-grey font-14 details">
                  High Volume price
                </p>
              </div>
            </div>
            <div className="flex items-center">
              <div className="status w-auto text-center mr-2">
                <span className="dots-for-status green-dot " />
                {data.activeStatus ? "Available" : "Unavailable"}
              </div>
            </div>
            <div className="flex items-center justify-between">
              <label className="switch mr-6 relative inline-block">
                <input
                  type="checkbox"
                  checked={data.activeStatus}
                  onChange={(e) => this.handleToggle(index, e.target.checked)}
                />
                <span className="slider round absolute" />
              </label>
              <div className="right-icon flex items-center justify-center">
                <i className="las la-ellipsis-h" />
              </div>
            </div>
          </div>
        ))}
      </>
    );
  }
}
//@ts-ignore
export default withRouter(CatalogueRelated);
