import React from "react";
import Dropzone from "react-dropzone";

import CatalogueImageController, {
    Props
}
    from "./CatalogueImageController.web";

export const configJSON = require("./config");

export default class CatalogueImage extends CatalogueImageController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>
                <div className="admin-wrapper">
                    <div className="h-44 w-44 border-dashed border-1 bg-gray-100">
                        <Dropzone onDrop={this.handleOnDrop} accept="image/*" minSize={0}
                            maxSize={5242880}
                            multiple>
                            {({ getRootProps, getInputProps }) => (
                                <div {...getRootProps()}>
                                    <input {...getInputProps()} />
              Click me to upload a file!
                                </div>
                            )}
                        </Dropzone>
                    </div>
                    <div>
                        <ul className="list-group mt-2">

                            {this.state.acceptedFiles.length > 0 && this.state.acceptedFiles.map((acceptedFile: any) => (
                                <li className="list-group-item list-group-item-success">
                                    {console.log(acceptedFile)}
                                    {acceptedFile.name}
                                    <img src={URL.createObjectURL(acceptedFile)} alt="image" />
                                </li>
                            ))}
                        </ul>
                    </div>

                </div>
            </>

        );
    }
}
