// @ts-nocheck

import React from "react";

// Customizable Area Start
import { withRouter, Link } from "react-router-dom";
import { filled, lock, okydLogo,superAdminImg } from "./assets";

import "./EmailAccountLogin.css";

//@ts-ignore
// Customizable Area End

import EmailAccountLoginController, {
  Props,
} from "./EmailAccountLoginController.web";

class EmailAccountLoginBlockemail extends EmailAccountLoginController {
  // Customizable Area Start
  // Customizable Area End

  constructor(props: Props) {
    super(props);
    // Customizable Area Start
    // Customizable Area End
  }

  render() {
    return (
      <div className="outer-wrapper">
        <div className="upper-wrapper">
          {/* <img  src={this.props.match?.params?.id ? lock : okydLogo} alt="" className="okyd-logo" /> */}
          {this.props.match.url.includes('/login') ?
          <img  src={this.props.match?.params?.id ? lock : okydLogo} alt="" className="okyd-logo" />
          :<img src={superAdminImg} alt="superAdminImg" className="admin-img" /> 
          }
        </div>
        <div className="botton-wrapper">
          <div className="inner-box">
              {this.props.match.url === '/login' ?
            <h4 className="mb-4">Sign In to Get Started</h4>
            :
             <h4 className="mb-4">Welcome back Admin Sign In to get started</h4>
            }
              <p className="sub-heading">Login to continue</p>

            <form onSubmit={(e) =>{e.preventDefault(), this.doEmailLogIn()}}>
              <div className="field-wrapper">
                <label>Email</label>
                <input type="email" placeholder="Enter Email Id"    {...this.txtInputEmailProps}/>
                <img src={filled} alt="" />
              </div>
                {this.state.errorEmail && <div className="text-red-600 mb-3">{this.state.errorEmail}</div>}
              <div className="field-wrapper">
                <label>Password</label>
                <input type="password" placeholder="Enter password" {...this.txtInputPasswordProps} />
                <img src={lock} alt="" />
              </div>
                 {this.state.errorPassword && <div className="text-red-600 mb-3">{this.state.errorPassword}</div>}
              <div className="links-wrapper">
                <div className="relative radios-one">
                  <input type="radio" id="test1" name="radio-group" 
                  checked={this.state.checkedRememberMe}
                     {...this.btnRememberMeProps} 
                      />
                  <label htmlFor="test1">Remember Me</label>
                </div>
                <div>
                  <span>
                    <Link to='/forget-password'>Recover Password</Link>
                  </span>
                </div>
              </div>
              <div>
                <button
                  type="submit"
                  className="button w-96 op-btn-primary mb-3"

                  // onClick={() => this.props.history.push("select-domain")}
                  //  {...this.btnEmailLogInProps}
                  // onClick={() => this.doEmailLogIn()}
                  // onClick={() => console.log('click')}
                >
                  Sign In
                </button>
                <button
                  className="button w-96  op-btn-secondary"
                  onClick={() =>
                    this.props.history.push("email-account-signup")
                  }
                >
                  Create Account
                </button>
              </div>
            </form>
          </div>
        </div>
        <p className="copyright-info">copyright 2020 okyd</p>
      </div>
    );
  }
}

// Customizable Area Start
// Customizable Area End
export default withRouter(EmailAccountLoginBlockemail);
