import React from "react";
/**
 * Components
 */
import { withRouter } from "react-router-dom";
import { Formik, Field, Form, FieldArray, ErrorMessage } from "formik";
import "react-datepicker/dist/react-datepicker.css";
// import Modal from "@material-ui/core/Modal";
import Select from "react-select";
import Modal from "../../../components/src/GenericModal.web";

/**
 * Controller
 */
import CatalogueController, { Props } from "./CatalogueController.web";
/**
 * Assets
 */

// import { validationSchema } from './Categoriessubcategories.web.validation';

export const configJSON = require("./config");

const variationOption = [
  { value: "x", label: "X" },
  { value: "xxl", label: "XXL" },
];

// const [selectedstatusOption, setSelectedstatusOption] = React.useState();

class CatalogueVariation extends CatalogueController {
  constructor(props: Props) {
    super(props);
    // Customizable Area Start

    // Customizable Area End
  }
  // Customizable Area Start

  // Customizable Area End
  render() {
    return (
      <>
        <div className="">
          <div className="flex">
            <div className="w-60">
              <label htmlFor="addVarition">Add Variation</label>
              <Select
                // value={selectedstatusOption}
                onChange={(e: any) => console.log("e", e)}
                options={variationOption}
              />
            </div>
            <div className="ml-auto">
              <button
                type="button"
                className="button op-btn-primary-light "
                onClick={() => this.handleToggleAddVariationModal()}
              >
                Add New Variation Type
              </button>
            </div>
          </div>

          <div className="flex  mt-12">
            <button
              type="submit"
              className="button op-btn-primary ml-auto mr-2"
            >
              Add Product
            </button>
            <button
              type="button"
              className="button op-btn-light"
              // onClick={() =>
              //   this.handleToggleAddProductModal()
              // }
            >
              Cancel
            </button>
          </div>
        </div>

        {/* Create Variation Modal */}
        <Modal
          visible={this.state.addNewVariationModal}
          onClose={this.handleToggleAddVariationModal}
        >
          <div className="flex items-center mb-10">
            <h4 className="text-xl text-secondary-base font-bold">
              Create New Variation Type
            </h4>
          </div>
          <div className="admin-body-content px-8 py-12">
            <Formik
              initialValues={{
                productId: "",
                name: "",
                description: "",
                discount: "",
                active: "",
                private: "",
                default_product: "",
                meta_title: "",
                meta_description: "",
                friends: ["jared", "ian", "brent"],
              }}
              validationSchema={""}
              onSubmit={
                (values) => console.log("values", values)
                //   (
                //   values: FormValues,
                //   { setSubmitting }: FormikHelpers<FormValues>
                // ) => {
                //   setTimeout(() => {
                //     // alert(JSON.stringify(values, null, 2));
                //     console.log(values);
                //     this.createProduct(values);
                //     setSubmitting(false);
                //   }, 500);
                // }
              }
              enableReinitialize
              render={({ values }) => {
                // console.log(values);
                return (
                  <Form>
                    <div className="flex">
                      <div className="field-wrapper flex-grow mr-4">
                        <label htmlFor="name">Name</label>
                        <Field
                          id="name"
                          name="name"
                          placeholder="Name of option group, eg Color, Size etc"
                        />
                        {/* <img src={filled} alt="" /> */}
                        <span className="error">
                          <ErrorMessage name="name" />
                        </span>
                      </div>
                      <div className="field-wrapper flex-grow">
                        <label htmlFor="name">Code</label>
                        <Field
                          id="name"
                          name="name"
                          placeholder="Unique code of this varient"
                        />
                        {/* <img src={filled} alt="" /> */}
                        <span className="error">
                          <ErrorMessage name="name" />
                        </span>
                      </div>
                    </div>
                    <div className="flex">
                      <div>
                        <label className="container-checkbox">
                          <input type="checkbox" />
                          <span className="checkmark" />
                        </label>
                      </div>
                      <div>Is required by default</div>
                    </div>
                    <div className="flex items-center my-10">
                      <h4 className="text-xl text-secondary-base font-bold">
                        List of values
                      </h4>
                    </div>
                    <FieldArray
                      name="friends"
                      render={(arrayHelpers) => (
                        <div>
                          {values.friends && values.friends.length > 0 ? (
                            values.friends.map((friend, index) => (
                              <div key={index}>
                                <div className="field-wrapper flex-grow">
                                  <label htmlFor={`friends.${index}`}>
                                    Variation {index + 1}
                                  </label>
                                  <div className="flex">
                                    <Field
                                      id={`friends.${index}`}
                                      name={`friends.${index}`}
                                      placeholder="Unique code of this varient"
                                    />
                                    <button
                                      type="button"
                                      onClick={() => arrayHelpers.remove(index)} // remove a friend from the list
                                    >
                                      -
                                    </button>
                                  </div>
                                  {/* <img src={filled} alt="" /> */}
                                  <span className="error">
                                    <ErrorMessage name={`friends.${index}`} />
                                  </span>
                                </div>
                                {/* <Field name={`friends.${index}`} /> */}
                              </div>
                            ))
                          ) : (
                            <button
                              type="button"
                              onClick={() => arrayHelpers.push("")}
                            >
                              {/* show this when user has removed all friends from the list */}
                              Add a friend
                            </button>
                          )}
                          <div className="flex">
                            <button
                              type="button"
                              className="m-auto border-solid border-4  border-gray-500 h-10 w-10 rounded-full"
                              onClick={() =>
                                arrayHelpers.insert(values.friends.length, "")
                              } // insert an empty string at a position
                            >
                              +
                            </button>
                          </div>
                        </div>
                      )}
                    />
                    <div className="flex items-center justify-between mt-12">
                      <button
                        type="button"
                        className="button op-btn-light"
                        onClick={() => this.handleToggleAddVariationModal()}
                      >
                        Cancel
                      </button>
                      <button
                        type="submit"
                        className="button op-btn-primary flex items-center"
                      >
                        Add Product
                      </button>
                    </div>
                  </Form>
                );
              }}
            />
          </div>
        </Modal>
      </>
    );
  }
}

//@ts-ignore
export default withRouter(CatalogueVariation);
