import React from "react";
/**
 * Components
 */
import { withRouter } from "react-router-dom";
import { Formik, Field, Form, FormikHelpers, ErrorMessage } from "formik";
import RefreshIcon from "@material-ui/icons/Refresh";
import "react-datepicker/dist/react-datepicker.css";
import Modal from "../../../components/src/GenericModal.web";
import { Switch } from "@material-ui/core";
import Select from "react-select";
import RichTextEditor from 'react-rte';


/**
 * Controller
 */
import CatalogueController, { Props } from "./CatalogueController.web";
import { uploadIcon } from "./assets";
/**
 * Assets
 */

// import { validationSchema } from './Categoriessubcategories.web.validation';

export const configJSON = require("./config");

interface FormValues {
  roleId: string | null | undefined;
  roleName: string | null | undefined;
  module_roles_attributes: any;
}

const statusOption = [
  { value: "active", label: "Active" },
  { value: "inactive", label: "Inactive" },
];
const brandsOption = [
  { value: "sony", label: "Sony" },
  { value: "samsung", label: "Samsung" },
  { value: "apple", label: "Apple" },
];
const catagoryOption = [
  { value: "electronic", label: "Electronic" },
  { value: "furniture", label: "Furniture" },
  { value: "sport", label: "Sport" },
];
// const [selectedstatusOption, setSelectedstatusOption] = React.useState();

class CaralogueProductDetails extends CatalogueController {
  constructor(props: Props) {
    super(props);
    // Customizable Area Start

    // Customizable Area End
  }
  // Customizable Area Start

  // Customizable Area End
  render() {
    return (
      <>
        <div className="admin-page-title-wrapper justify-between">
          <div className="flex items-center">
            <button
              type="button"
              className="op-btn-primary-transparent"
              // onClick={this.onMenuToggle}
            >
              {/* <img src={menuIcon} alt="Menu" className="h-9" /> */}
            </button>
            <p className="admin-page-title">{configJSON.title}</p>
          </div>
        </div>
        {/* Page Content */}
        <div className="admin-body-content px-8 py-12">
          <Formik
            initialValues={{
              productId: "",
              name: "",
              description: "",
              discount: "",
              active: "",
              private: "",
              default_product: "",
              meta_title: "",
              meta_description: "",
            }}
            validationSchema={""}
            onSubmit={
              (values) => console.log("values", values)
              //   (
              //   values: FormValues,
              //   { setSubmitting }: FormikHelpers<FormValues>
              // ) => {
              //   setTimeout(() => {
              //     // alert(JSON.stringify(values, null, 2));
              //     console.log(values);
              //     this.createProduct(values);
              //     setSubmitting(false);
              //   }, 500);
              // }
            }
            enableReinitialize
            render={({ values, setFieldValue, isSubmitting }) => {
              // console.log(values);
              return (
                <Form>
                  {/* <GenericBackdrop
                            open={this.state.loader}
                            /> */}
                  <Field id="productId" name="productId" type="hidden" />
                  <div className="grid grid-cols-2 gap-12 pb-8">
                    <div>
                      <div className="flex -mx-4">
                        <div className="w-3/12 px-4 mb-10">
                          <input
                            accept="image/*"
                            className="hidden"
                            id="productImage"
                            multiple
                            type="file"
                            onChange={
                              (e: any) => console.log("Image", e.target?.files?.[0])
                              // this.handleImageUpload
                            }
                          />
                          <div className="field-wrapper ">
                            <label htmlFor="productName">Product Image</label>
                          </div>
                          <label htmlFor="productImage">
                            <img
                              src={
                                this.state.selectedFile &&
                                this.state.selectedFile !== null
                                  ? this.state.selectedFile
                                  : uploadIcon
                              }
                              alt="Upload"
                              className="w-full"
                            />
                          </label>
                          <span className="error">
                            <ErrorMessage name="productImage" />
                          </span>
                        </div>
                        <div className="w-9/12 px-4">
                          <div className="field-wrapper catalog-input-position">
                            <label htmlFor="name">Product name</label>
                            <Field
                              id="name"
                              name="name"
                              placeholder="Start typing…"
                            />
                              <i className="las la-cube catalog-image-input" />
                            <span className="error">
                              <ErrorMessage name="name" />
                            </span>
                          </div>
                          <div className="field-wrapper">
                            <label htmlFor="sku">Sku</label>
                            <Field
                              id="sku"
                              name="sku"
                              placeholder="Start typing…"
                            />
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="discount" />
                            </span>
                          </div>
                        </div>
                      </div>
                      <div>
                        <div className="field-wrapper">
                          <label htmlFor="meta_title">Brand</label>
                          <Select
                            // value={selectedstatusOption}
                            onChange={(e: any) => console.log("e", e)}
                            options={brandsOption}
                          />
                          {/* <Field
                            id="meta_title"
                            name="meta_title"
                            placeholder="Type meta title. This will be added to the HTML"
                          />
                          <span className="error">
                            <ErrorMessage name="meta_title" />
                          </span> */}
                        </div>
                        <div className="field-wrapper">
                          <label htmlFor="meta_description">Category</label>
                          <Select
                            // value={selectedstatusOption}
                            isMulti
                            onChange={(e: any) => console.log("e", e)}
                            options={catagoryOption}
                          />
                          {/* <Field
                            id="meta_description"
                            name="meta_description"
                            placeholder="Type meta description. This will be added to the HTML"
                          />
                          <span className="error">
                            <ErrorMessage name="meta_description" />
                          </span> */}
                        </div>
                      </div>
                    </div>
                    <div>
                      <div className="field-wrapper h-72 ">
                        <label htmlFor="description">Description</label>
                        <Field
                          id="description"
                          name="description"
                          placeholder="Start typing…"
                          className="mb-0"
                        />
                         <RichTextEditor
                              className="oked-editor"
                              value={this.state.editor}
                              placeholder="Start typing…"
                              onChange={(value: any) => {
                                this.setState({ editor: value });
                                setFieldValue("description", value.toString('html'));
                              }}
                            />
                        {/* <img src={filled} alt="" /> */}
                        <span className="error">
                          <ErrorMessage name="description" />
                        </span>
                      </div>
                      <div className="flex">
                        <div className="field-wrapper mr-8">
                          <label htmlFor="active">Is Active?</label>
                          <div>
                            <Switch
                              name="active"
                              onChange={(e: any): void => {
                                console.log(e.target.checked);
                                setFieldValue("active", e.target.checked);
                              }}
