import React from "react";
import ReactDropzone from "react-dropzone";

import CatalogueImageController, {
    Props,
} from "./CatalogueImageController.web";

export const configJSON = require("./config");

export default class CatalogueImage extends CatalogueImageController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>
                <div className="admin-wrapper">
                    {/* Sidebar */}
                    <ReactDropzone accept="image/*" onDrop={this.onPreviewDrop} />
                    {this.state.files.length > 0 && (
                        <div>
                            <h3>Previews</h3>
                            {this.state.files}
                            {/* {this.state.files.map((file) => (
                                <img alt="Preview" key={file.preview} src={file.preview} />
                            ))} */}
                        </div>
                    )}
                </div>
            </>
        );
    }
}
