import React from "react";
/**
 * Components
 */
import { withRouter, Link } from "react-router-dom";
import { Formik, Field, Form, FormikHelpers, ErrorMessage } from "formik";
import RefreshIcon from "@material-ui/icons/Refresh";
import Snackbar from "@material-ui/core/Snackbar";
import MuiAlert from "@material-ui/lab/Alert";
import "react-datepicker/dist/react-datepicker.css";
import Modal from "../../../components/src/GenericModal.web";
import GenericBackdrop from "../../../components/src/GenericBackdrop";
import Sidebar from "../../../components/src/Sidebar";
import { img } from "./assets";
import Select from "react-select";

/**
 * Controller
 */
import CatalogueController, { Props } from "./CatalogueController.web";
import { isEmpty } from "lodash";
import { Switch } from "@material-ui/core";
import CaralogueProductDetails from "./CaralogueProductDetails.web";
import CatalogueVariation from "./CatalogueVariation.web";
import CatalogueImage from "./CatalogueImage.web";
import CataloguePricing from "./CataloguePricing.web";
import CatalogueRelated from "./CatalogueRelated.web";

/**
 * Assets
 */

// import { validationSchema } from './Categoriessubcategories.web.validation';

export const configJSON = require("./config");

interface FormValues {
  roleId: string | null | undefined;
  roleName: string | null | undefined;
  module_roles_attributes: any;
}

const Tabs = ({ color }: any) => {
  const [openTab, setOpenTab] = React.useState(1);
  return (
    <>
      <div className="flex flex-wrap">
        <div className="w-full">
          <ul
            className="flex mb-0 list-none flex-wrap pt-3 pb-4 flex-row"
            role="tablist"
          >
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 1
                    ? "text-white bg-" + color + "-600"
                    : "text-" + color + "-600 bg-white")
                }
                onClick={(e) => {
                  e.preventDefault();
                  setOpenTab(1);
                }}
                data-toggle="tab"
                href="#link1"
                role="tablist"
              >
                Primary Details
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 2
                    ? "text-white bg-" + color + "-600"
                    : "text-" + color + "-600 bg-white")
                }
                onClick={(e) => {
                  e.preventDefault();
                  setOpenTab(2);
                }}
                data-toggle="tab"
                href="#link2"
                role="tablist"
              >
                Variations
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 3
                    ? "text-white bg-" + color + "-600"
                    : "text-" + color + "-600 bg-white")
                }
                onClick={(e) => {
                  e.preventDefault();
                  setOpenTab(3);
                }}
                data-toggle="tab"
                href="#link3"
                role="tablist"
              >
                Pricing
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 4
                    ? "text-white bg-" + color + "-600"
                    : "text-" + color + "-600 bg-white")
                }
                onClick={(e) => {
                  e.preventDefault();
                  setOpenTab(4);
                }}
                data-toggle="tab"
                href="#link4"
                role="tablist"
              >
                Images
              </a>
            </li>
            <li className="-mb-px mr-2 last:mr-0 flex-auto text-center">
              <a
                className={
                  "text-xs font-bold uppercase px-5 py-3 shadow-lg rounded block leading-normal " +
                  (openTab === 5
                    ? "text-white bg-" + color + "-600"
                    : "text-" + color + "-600 bg-white")
                }
                onClick={(e) => {
                  e.preventDefault();
                  setOpenTab(5);
                }}
                data-toggle="tab"
                href="#link3"
                role="tablist"
              >
                RELATED
              </a>
            </li>
          </ul>

          <div className="relative flex flex-col min-w-0 break-words bg-white w-full mb-6  rounded">
            <div className="">
              <div className="tab-content tab-space">
                <div className={openTab === 1 ? "block" : "hidden"} id="link1">
                  <CaralogueProductDetails />
                </div>
                <div className={openTab === 2 ? "block" : "hidden"} id="link2">
                  <CatalogueVariation />
                </div>
                <div className={openTab === 3 ? "block" : "hidden"} id="link3">
                  <CataloguePricing />
                </div>
                <div className={openTab === 4 ? "block" : "hidden"} id="link4">
                  <CatalogueImage />
                </div>
                <div className={openTab === 5 ? "block" : "hidden"} id="link5">
                  <CatalogueRelated />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

const variationOption = [
  { value: "x", label: "X" },
  { value: "xxl", label: "XXL" },
];

class CaralogueAddProduct extends CatalogueController {
  constructor(props: Props) {
    super(props);
    // Customizable Area Start
    // Customizable Area End
  }
  // Customizable Area Start
  // Customizable Area End
  render() {
    return (
      <>
        {/* Admin Body */}
        <div
          className={
            this.state.menuCollapsed ? "admin-body-collapsed" : "admin-body"
          }
        >
          <div className=" bg-off-white mt-8">
            <div className="flex justify-between mb-6">
              <div className="flex items-center">
                <div className="h-w-36 rounded-lg flex items-center justify-center bg-grey mr-6">
                  <i className="las la-arrows-alt-h" />
                </div>
                <h2 className="font-20 font-bold">Add Products</h2>
              </div>
            </div>

            <div className="pt-8 pl-8 pr-16 pb-8 bg-white min-height-tab-wrapper">
              <Tabs color="blue" />
              {/* <div className="modal-footer flex justify-between">
                    <button
                      type="button"
                      className="button op-btn-primary w-auto"
                    >
                      Add Products
                    </button>
                    <button
                      type="button"
                      className="button okyd-grey-btn"
                      onClick={() => this.handleToggleAddProductModal()}
                    >
                      Cancel
                    </button>
                  </div> */}
            </div>
          </div>
        </div>
      </>
    );
  }
}

//@ts-ignore
export default withRouter(CaralogueAddProduct);
