import React from "react";
import Dropzone from "react-dropzone";

import CatalogueImageController, {
    Props
}
    from "./CatalogueImageController.web";

export const configJSON = require("./config");

export default class CatalogueImage extends CatalogueImageController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>
                <div className="admin-wrapper">

                    <h1>hii</h1>
                    <Dropzone onDrop={this.handleOnDrop} accept="image/png" minSize={0}
                        maxSize={5242880}
                        multiple>{dropzoneProps => {
                            return (
                                <div>
                                    <p>Drop some files here</p>
                                </div>
                            );
                        }}</Dropzone>

                </div>
            </>

        );
    }
}
