import { IBlock } from "../../../framework/src/IBlock";
import { Message } from "../../../framework/src/Message";
import { BlockComponent } from "../../../framework/src/BlockComponent";
import { runEngine } from "../../../framework/src/RunEngine";
import MessageEnum, {
  getName,
} from "../../../framework/src/Messages/MessageEnum";
import {isEmpty} from 'lodash'

// Customizable Area Start
import { imgPasswordInVisible, imgPasswordVisible } from "./assets";
// Customizable Area End

export const configJSON = require("./config");

export interface Props {
  navigation: any;
  id: string;
}

export interface S {
  // Customizable Area Start
  fullName: string;
  companyName: string;
  phoneNumber: string;
  email: string;
  userType: string;
  domainName: string;
  address: string;
  country: string;
  teamMemberOkyd: string;
  natureOfBussiness: string;
  clientTarget: string;
  errorMandatory: string;
  errorEmail: string;
  errorCompanyName: string;
  errorDomainName: string;
  errorFullName: string;
  errorPhnNumber: string;
  errorAddress: string;
  errorCountry: string;
  activated:boolean;

  // Customizable Area End
}

export interface SS {
  // Customizable Area Start
  id: any;
  // Customizable Area End
}

export default class EmailAccountRegistrationController extends BlockComponent<
  Props,
  S,
  SS
> {
  // Customizable Area Start
  arrayholder: any[];
  passwordReg: RegExp;
  emailReg: RegExp;
  stringReg: RegExp;
  phoneReg: RegExp;
  numberReg: RegExp;
  createAccountApiCallId: any;
  createNewAccountApiCallId: any;

  validationApiCallId: string = "";

  imgPasswordVisible: any;
  imgPasswordInVisible: any;

  labelHeader: any;
  labelFirstName: string;
  lastName: string;
  labelEmail: string;
  labelPassword: string;
  labelRePassword: string;
  labelLegalText: string;
  labelLegalTermCondition: string;
  labelLegalPrivacyPolicy: string;
  btnTextSignUp: string;

  currentCountryCode: any;
  // Customizable Area End

  constructor(props: Props) {
    super(props);
    this.subScribedMessages = [
      getName(MessageEnum.RestAPIResponceMessage),
      getName(MessageEnum.NavigationPayLoadMessage),
      getName(MessageEnum.CountryCodeMessage),
    ];
    this.receive = this.receive.bind(this);
    this.isStringNullOrBlank = this.isStringNullOrBlank.bind(this);

    runEngine.attachBuildingBlock(this, this.subScribedMessages);

    this.state = {
      // Customizable Area Start
      email: "",
      companyName: "",
      domainName: "",
      fullName: "",
      phoneNumber: "",
      address: "",
      country: "",
      teamMemberOkyd: "",
      natureOfBussiness: "",
      clientTarget: "",
      userType: "Subscriber",
      activated:true,
      errorMandatory: "",
      errorEmail: "",
      errorCompanyName: "",
      errorDomainName: "",
      errorFullName: "",
      errorPhnNumber: "",
      errorAddress: "",
      errorCountry: "",
      // Customizable Area End
    };

    // Customizable Area Start
    this.arrayholder = [];
    this.passwordReg = new RegExp("\\w+");
    this.emailReg = new RegExp("\\w+");
    this.stringReg = new RegExp(/^[A-Za-z]+$/);
    this.phoneReg = new RegExp(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/);
    this.numberReg = new RegExp(/^[0-9]*$/);


    this.imgPasswordVisible = imgPasswordVisible;
    this.imgPasswordInVisible = imgPasswordInVisible;

    this.labelHeader = configJSON.labelHeader;
    this.labelFirstName = configJSON.labelFirstName;
    this.lastName = configJSON.lastName;
    this.labelEmail = configJSON.labelEmail;
    this.labelPassword = configJSON.labelPassword;
    this.labelRePassword = configJSON.labelRePassword;
    this.labelLegalText = configJSON.labelLegalText;
    this.labelLegalTermCondition = configJSON.labelLegalTermCondition;
    this.labelLegalPrivacyPolicy = configJSON.labelLegalPrivacyPolicy;
    this.btnTextSignUp = configJSON.btnTextSignUp;
    // Customizable Area End
  }

  async receive(from: string, message: Message) {
    // Customizable Area Start
    if (getName(MessageEnum.RestAPIResponceMessage) === message.id) {
      const apiRequestCallId = message.getData(
        getName(MessageEnum.RestAPIResponceDataMessage)
      );

      var responseJson = message.getData(
        getName(MessageEnum.RestAPIResponceSuccessMessage)
      );

      var errorReponse = message.getData(
        getName(MessageEnum.RestAPIResponceErrorMessage)
      );

      // console.log('apiRequestCallId',apiRequestCallId)
      //   console.log('createNewAccountApiCallId',this.createNewAccountApiCallId)
      if (apiRequestCallId && responseJson) {
        if (apiRequestCallId === this.validationApiCallId) {
          this.arrayholder = responseJson.data;

          if (this.arrayholder && this.arrayholder.length !== 0) {
            let regexData = this.arrayholder[0];

            if (regexData.password_validation_regexp) {
              this.passwordReg = new RegExp(
                regexData.password_validation_regexp
              );
            }

            if (regexData.password_validation_rules) {
              //   this.setState({
              //     passwordHelperText: regexData.password_validation_rules,
              //   });
            }

            if (regexData.email_validation_regexp) {
              this.emailReg = new RegExp(regexData.email_validation_regexp);
            }
          }
        } else if (apiRequestCallId === this.createAccountApiCallId) {
          if (!responseJson.errors) {
            const msg: Message = new Message(
              getName(MessageEnum.AccoutResgistrationSuccess)
            );

            msg.addData(
              getName(MessageEnum.NavigationPropsMessage),
              this.props
            );

            this.send(msg);
          } else {
            //Check Error Response
            this.parseApiErrorResponse(responseJson);
          }

          this.parseApiCatchErrorResponse(errorReponse);
        }

        else if (apiRequestCallId === this.createNewAccountApiCallId) {
          if (responseJson.errors[0].account === "Email Already exist" ) {
           console.log('responseJson',responseJson)
           alert(responseJson.errors[0].account)
           //errors: [{account: "Email Already exist"}]
          // 0: {account: "Email Already exist"}
          } else{
            localStorage.setItem('email',this.state.email)
            // @ts-ignore
             this.props.history.push("new-password")
          }
        }
      }
    }

    if (getName(MessageEnum.NavigationPayLoadMessage) === message.id) {
      const otpAuthTkn = message.getData(
        getName(MessageEnum.AuthTokenDataMessage)
      );
      if (otpAuthTkn && otpAuthTkn.length > 0) {
        // this.setState({ otpAuthToken: otpAuthTkn });
        // runEngine.debugLog("otpAuthTkn", this.state.otpAuthToken);
        runEngine.unSubscribeFromMessages(this as IBlock, [message.id]);
      }
    }

    if (getName(MessageEnum.CountryCodeMessage) === message.id) {
      var selectedCode = message.getData(
        getName(MessageEnum.CountyCodeDataMessage)
      );

      if (selectedCode !== undefined) {
        // this.setState({
        //   countryCodeSelected:
        //     selectedCode.indexOf("+") > 0
        //       ? selectedCode.split("+")[1]
        //       : selectedCode,
        // });
      }
    }
    // Customizable Area End
  }

  // Customizable Area Start
  goToPrivacyPolicy() {
    const msg: Message = new Message(
      getName(MessageEnum.NavigationPrivacyPolicyMessage)
    );
    msg.addData(getName(MessageEnum.NavigationPropsMessage), this.props);
    this.send(msg);
  }

  goToTermsAndCondition() {
    const msg: Message = new Message(
      getName(MessageEnum.NavigationTermAndConditionMessage)
    );
    msg.addData(getName(MessageEnum.NavigationPropsMessage), this.props);
    this.send(msg);
  }

  isStringNullOrBlank(str: string) {
    return str === null || str.length === 0;
  }

  isValidEmail(email: string) {
    return this.emailReg.test(email);
  }

  createAccount(): boolean {
     if (
      this.state.email === null ||
      this.state.email.length === 0 ||
      isEmpty(this.state.email)
    ){
      this.setState({
          errorEmail:configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }else if( !this.emailReg.test(this.state.email)){
      this.setState({
          errorEmail:configJSON.errorEmailNotValid
      })
      return false;
    }

     if (isEmpty(this.state.companyName)){
      this.setState({
          errorCompanyName :configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }

     if (isEmpty(this.state.domainName)){
      this.setState({
          errorDomainName :configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }else if( !this.state.domainName.includes('.')){
      this.setState({
          errorDomainName:configJSON.errorDomainNotValid
      })
      return false;
    }

    if (isEmpty(this.state.fullName)){
      this.setState({
          errorFullName :configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }else if( !this.state.fullName.match(this.stringReg)){
      this.setState({
          errorFullName:configJSON.errorStingOnly
      })
      return false;
    }

    if (isEmpty(this.state.phoneNumber)){
      this.setState({
          errorPhnNumber :configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }else if( !this.state.phoneNumber.match(this.phoneReg)){
      this.setState({
          errorPhnNumber:configJSON.errorPhoneNumberType
      })
      return false;
    }else if( this.state.phoneNumber.length>10){
      this.setState({
          errorPhnNumber:configJSON.errorPhoneNumberMax
      })
      return false;
    }

     if (isEmpty(this.state.address)){
      this.setState({
          errorAddress :configJSON.errorAllFieldsAreMandatory
      })
      return false;
    }else if( this.state.address.length > 250){
      this.setState({
          errorAddress:configJSON.errorAddressMax
      })
      return false;
    }

   console.log('this.state',this.state)
    const header = {
