import React from "react";
import { Link } from "react-router-dom";
import { logo, dashboardIcon, menuIcon } from "./Sidebar.assets";

type PropTypes = {
  menuCollapsed: boolean;
};

const Sidebar = (props: PropTypes) => {
  const { menuCollapsed } = props;

  return (
    <div
      className={menuCollapsed ? "admin-sidebar-collapsed" : "admin-sidebar"}
    >
      {/* Logo */}
      <div className="flex items-center">
        <img src={logo} alt="OKYD admin" className="mr-5 h-8" />
        {!menuCollapsed && "OKYD Admin"}
      </div>
      {/* Sidebar Menu */}
      <div className="admin-sidebar-menu">
        <Link to="/">
          <div className="admin-sidebar-menu-item">
            <img src={dashboardIcon} alt="Dashboard" className="h-5" />
            <p
              className={`admin-sidebar-menu-item-text ml-7 ${
                menuCollapsed ? "hidden" : "block"
              }`}
            >
              Dashboard
            </p>
          </div>
        </Link>
        <Link to="/audit-trail">
          <div className="admin-sidebar-menu-item">
            <img src={dashboardIcon} alt="Audit Trail" className="h-5" />
            <p
              className={`admin-sidebar-menu-item-text ml-7 ${
                menuCollapsed ? "hidden" : "block"
              }`}
            >
              Audit Trail
            </p>
          </div>
        </Link>
        <Link to="/roles-permissions">
          <div className="admin-sidebar-menu-item">
            <img src={dashboardIcon} alt="" className="h-5" />
            <p
              className={`admin-sidebar-menu-item-text ml-7 ${
                menuCollapsed ? "hidden" : "block"
              }`}
            >
              Roles & Permissions
            </p>
          </div>
        </Link>
        <Link to="/categories">
          <div className="admin-sidebar-menu-item">
            <img src={dashboardIcon} alt="" className="h-5" />
            <p
              className={`admin-sidebar-menu-item-text ml-7 ${
                menuCollapsed ? "hidden" : "block"
              }`}
            >
              Categories
            </p>
          </div>
        </Link>
        <div className="admin-sidebar-menu-item">
          <img src={dashboardIcon} alt="" className="h-5" />
          <p
            className={`admin-sidebar-menu-item-text ml-7 ${
              menuCollapsed ? "hidden" : "block"
            }`}
          >
            Menu Item
          </p>
        </div>
        <div className="admin-sidebar-menu-item">
          <img src={dashboardIcon} alt="" className="h-5" />
          <p
            className={`admin-sidebar-menu-item-text ml-7 ${
              menuCollapsed ? "hidden" : "block"
            }`}
          >
            Menu Item
          </p>
        </div>
      </div>
      <div className="admin-sidebar-account">
        <div className="flex items-center py-3">
          <img src={menuIcon} alt="" className="h-9" />
          <p
            className={`admin-sidebar-menu-item-text ml-4 ${
              menuCollapsed ? "hidden" : "block"
            }`}
          >
            User Name
          </p>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
