  async receive(from: string, message: Message) {
    // Customizable Area Start
    // runEngine.debugLog("Message Recived", message);

    if (message.id === getName(MessageEnum.AccoutLoginSuccess)) {
      let value = message.getData(getName(MessageEnum.AuthTokenDataMessage));

      this.showAlert(
        "Change Value",
        "From: " + this.state.txtSavedValue + " To: " + value
      );

      this.setState({
        txtSavedValue: value,
      });
    }

    // Customizable Area End

    if (getName(MessageEnum.RestAPIResponceMessage) === message.id) {
      var responseJson = message.getData(
        getName(MessageEnum.RestAPIResponceSuccessMessage)
      );
      if (responseJson && !responseJson.errors && responseJson.data) {
        const apiRequestCallId = message.getData(
          getName(MessageEnum.RestAPIResponceDataMessage)
        );
        if (apiRequestCallId != null) {
          // Store All Members data
          if (
            apiRequestCallId === this.getAllMembersApiCallId &&
            responseJson !== undefined
          ) {
            this.setState({
              membersData: responseJson.data,
            });
          }

          // Store All Logs data
          if (
            apiRequestCallId === this.getTeamMemberApiCallId &&
            responseJson !== undefined
          ) {
            this.setState({
              getTeamMembetList: responseJson.data,
            });
          }

          // Store trails Of Team Member detaills
          if (
            apiRequestCallId === this.trailsOfTeamMemberApiCallId &&
            responseJson !== undefined
          ) {
            this.setState({
              trailsOfTeamMemberData: {
                data: responseJson.data,
                count: responseJson.meta.total_record,
                name: responseJson.data[0].attributes.account_detail.name,
              },
            });
          }

          if (
            apiRequestCallId === this.deleteTeamMemberApiCallId 
          ) {
           console.log('deleteTeamMemberApiCallId',responseJson)
          }
          if (
            apiRequestCallId === this.inviteTeamMemberApiCallId 
          ) {
          //  console.log('inviteTeamMemberApiCallId',responseJson)
           this.openInviteTeamMemberModalHandler()

          }
          if (
            apiRequestCallId === this.rolesListingApiCallId &&
            responseJson !== undefined
          ) {
            const rolesList: RoleListDataType[] = [];
            responseJson.data.map((x: any) => {
              rolesList.push({
                value: x.attributes.id,
                label: x.attributes.name,
              })
            })
            this.setState({ rolesList });

          //  console.log('rolesListingApiCallId',responseJson)
          //  let data: any = [];
          //  for(let i=0 ; i<responseJson.data.length ; i++){
          //   data.push({
          //     id: responseJson.data.attributes.id,
          //     name: responseJson.data.attributes.name
          //   })
          //  }
          //  console.log("data data",data)
          //  this.setState({
          //    ...this.state,
          //    rolesList: data
          //  })
          }
        }
      } else {
        var errorReponse = message.getData(
          getName(MessageEnum.RestAPIResponceErrorMessage)
        );
        this.parseApiCatchErrorResponse(errorReponse);
      }
    }
  }

