import React from "react";
import Dropzone from "react-dropzone";

import CatalogueImageController, {
    Props
}
    from "./CatalogueImageController.web";

export const configJSON = require("./config");

export default class CatalogueImage extends CatalogueImageController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>
                <div className="admin-wrapper">

                    <h1>hii</h1>
                    <Dropzone onDrop={this.handleOnDrop} accept="image/png" minSize={0}
                        maxSize={5242880}
                        multiple>{({ getRootProps, getInputProps, isDragActive, isDragReject }) => {
                            //  const isFileTooLarge = rejectedFiles.length > 0 && rejectedFiles[0].size > 5242880;
                            return (
                                <div {...getRootProps()}>
                                    <input {...getInputProps()} />
                                    {!isDragActive && 'Click here or drop a file to upload!'}
                                    {isDragActive && !isDragReject && "Drop it like it's hot!"}
                                    {isDragReject && "File type not accepted, sorry!"}

                                    <div className="text-danger mt-2">
                                        File is too large.
                                        </div>

                                </div>
                            )
                        }
                        }</Dropzone>

                </div>
            </>

        );
    }
}
