import { TextField } from "@material-ui/core";

const ConvertOption = (data: any) => {
  let arr = [];
  if (data) {
    for (let i = 0; i < data; i++) {
      let obj = {
        ...data,
        value: data[i]?.attributes?.id,
        label: data[i]?.attributes?.id
      }
      arr.push(obj)
    }
  }
  return arr
}

