import React from "react";
/**
 * Components
 */
import { withRouter, Link } from "react-router-dom";
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import CircularProgress from '@material-ui/core/CircularProgress';
import Sidebar from "../../../components/src/Sidebar";
import Switch from "../../../components/src/SwitchWithOutForm";
/**
 * Controller
 */
import CategoriessubcategoriesController, { Props } from "./CategoriessubcategoriesController.web";
/**
 * Assets
 */
import {
  menuDownIcon,
  logsWighBgActiveIcon,
  timeWithBgIcon,
  deleteIcon,
  updateIcon,
  // searchIcon,
  addIcon,
  atozIcon,
} from "./assets";

export const configJSON = require("./config");

interface FormValues {
  roleId: string | null | undefined;
  roleName: string | null | undefined;
  module_roles_attributes: any;
}

class Categoriessubcategories extends CategoriessubcategoriesController {
  constructor(props: Props) {
    super(props);
    // Customizable Area Start
    // Customizable Area End
  }

  // Customizable Area Start
  // Table Row
  tableRow = (categoryDetails: any) => {
    return (
      <div className="at-list-row grid-cols-categoriesListLayout">
        <div className="at-list-col">
          <h4 className="at-col-value font-bold">
            {categoryDetails?.attributes?.name}
          </h4>
          <div className="at-col-title" dangerouslySetInnerHTML={{__html: categoryDetails?.attributes?.description}} />
        </div>
        <div className="at-list-col">
          {categoryDetails?.attributes?.sub_categories.length > 0 && (
            <React.Fragment>
              {categoryDetails?.attributes?.sub_categories.map((sub_category: any, index: number) => (
                <h4 className="text-primary-light-sm inline-block font-bold bg-light-200 py-1 px-3 rounded-md mr-2" key={String(index)}>
                  {sub_category.name}
                </h4>
              ))}
            </React.Fragment>
          )}
        </div>
        <div className="at-list-col">
          <h4 className="at-col-value">{categoryDetails?.attributes?.discount}{categoryDetails?.attributes?.discount > 0 ? '%' : ''}</h4>
          <p className="at-col-title">Discount</p>
        </div>
        {/* View Details */}
        <div className="at-list-col flex items-center justify-between">
          <div>
            <Switch
              name="active"
              checked={categoryDetails?.attributes?.active}
              onChange={(e: any): void => {
                this.updateActiveStatus(categoryDetails?.attributes?.id, e.target.checked);
              }}
            />
          </div>
          <div className="flex items-center justify-end">
            <button 
              type="button" 
              className="op-btn-transparent h-9" 
              onClick={() => {
                //@ts-ignore
                this.props?.history.push(`/categories/edit/${categoryDetails?.attributes?.id}`);
              }}
            >
              <img src={updateIcon} alt="Update" className="ml-3 h-9" />
            </button>
            <button type="button" className="op-btn-transparent h-9" onClick={() => {
              this.deleteCategoryHandler(categoryDetails?.attributes?.id)
              this.handleSortingName()}}>
              <img src={deleteIcon} alt="Delete" className="ml-3 h-9" />
            </button>
          </div>
        </div>
      </div>
    );
  }
  // Customizable Area End
  render() {
    return (
      <div className="admin-wrapper w-full">
        {/* Sidebar */}
        <Sidebar menuCollapsed={this.state.menuCollapsed}  />
        <div
          className={`
            ${
