import React from "react";
import Dropzone from "react-dropzone";
import { cloud } from "./assets";
import { withRouter } from "react-router-dom";
import CatalogueController, { Props } from "./CatalogueController.web";

export const configJSON = require("./config");

class CatalogueImage extends CatalogueController {
    constructor(props: Props) {
        super(props);
    }

    render() {
        return (
            <>

                <div className="flex flex-column ">
                    <div className="style-dropzone ">
                        <Dropzone
                            onDrop={this.handleOnDrop}
                            accept="image/*"
                            minSize={0}
                            maxSize={5242880}
                            multiple
                        >
                            {({ getRootProps, getInputProps }) => (
                                <div
                                    className="justify-center text-center"
                                    {...getRootProps()}
                                >
                                    <input {...getInputProps()} />

                                    <img
                                        src={cloud}
                                        width="168"
                                        height="112"
                                        className="text-gray-300 "
                                    />
                                    <p className="text-base mt-3">Drag Your Files To Upload</p>
                                    <p className="text-blue-600 text-sm">
                                        or click to the browser
                    </p>
                                </div>
                            )}
                        </Dropzone>
                    </div>
                    <div>
                        <div className="flex flex-row">
                            {this.state.acceptedFiles.length > 0 &&
                                this.state.acceptedFiles.map((acceptedFile: any) => (
                                    <span className="m-3">
                                        <img
                                            src={URL.createObjectURL(acceptedFile)}
                                            alt="image"
                                            width="350"
                                            height="250"
                                            className="rounded-t-lg "
                                        />
                                    </span>
                                ))}
                        </div>
                        <div className="flex items-center justify-between mt-12">
                            <button
                                type="button"
                                className="button op-btn-light"
                                onClick={() => this.handleToggleAddVariationModal()}
                            >
                                Cancel
                      </button>
                            <button
                                type="submit"
                                className="button op-btn-primary flex items-center"
                            >
                                Add Product
                      </button>
                        </div>
                    </div>
                </div>

            </>
        );
    }
}


//@ts-ignore
export default withRouter(CatalogueImage);
