import { IBlock } from "../../../framework/src/IBlock";
import { Message } from "../../../framework/src/Message";
import { BlockComponent } from "../../../framework/src/BlockComponent";
import MessageEnum, {
  getName,
} from "../../../framework/src/Messages/MessageEnum";
import { runEngine } from "../../../framework/src/RunEngine";

// Customizable Area Start
// Customizable Area End

export const configJSON = require("./config");

export interface Props {
  navigation: any;
  id: string;
  // Customizable Area Start
  // Customizable Area End
}

interface S {
  // Customizable Area Start
  arrayHolder: any;
  menuCollapsed: boolean;
  userType: string;
  token: string;
  displayProducts: any;
  allCheckboxStatus: any;
  addNewProductModal: any;
  addNewVariationModal: any;
  acceptedFiles: any;
  selectedFile: any;
  // Customizable Area End
}

interface SS {
  id: any;
}

export default class CatalogueController extends BlockComponent<Props, S, SS> {
  getProductApiCallId: any;
  getProductListingApiCallId: any;
  constructor(props: Props) {
    super(props);
    this.receive = this.receive.bind(this);

    // Customizable Area Start
    this.subScribedMessages = [
      getName(MessageEnum.RestAPIResponceMessage),
      getName(MessageEnum.SessionSaveMessage),
      getName(MessageEnum.SessionResponseMessage),
    ];

    this.state = {
      allCheckboxStatus: true,
      arrayHolder: [],
      menuCollapsed: false,
      acceptedFiles: [],
      userType: configJSON.subscriber,
      token: "",
      addNewVariationModal: false,
      displayProducts: [
        {
          id: 1,
          name: "Product Name",
          category: "Category Name",
          checkboxStatus: true,
          activeStatus: true,
          qty: 4550,
          currencySymbol: "$",
          price: {
            base: 200,
            wholesale: 180,
            highVol: 150,
          },
        },
        {
          id: 2,
          name: "Product Name",
          category: "Category Name",
          checkboxStatus: true,
          activeStatus: true,
          qty: 1550,
          currencySymbol: "$",
          price: {
            base: 220,
            wholesale: 150,
            highVol: 190,
          },
        },
        {
          id: 3,
          name: "Product Name",
          category: "Category Name",
          checkboxStatus: true,
          activeStatus: true,
          qty: 3550,
          currencySymbol: "$",
          price: {
            base: 400,
            wholesale: 380,
            highVol: 350,
          },
        },
      ],
      addNewProductModal: false,
      selectedFile: null,
    };
    // Customizable Area End
    runEngine.attachBuildingBlock(this as IBlock, this.subScribedMessages);
  }
  //   async componentDidMount() {
  //     super.componentDidMount();
  //     this.getToken();
  //     if (this.isPlatformWeb() === false) {
  //       this.props.navigation.addListener("willFocus", () => {
  //         this.getToken();
  //       });
  //     }
  //   }

  // Customizable Area Start

  async componentDidMount() {
    this.getProductListing();
  }

  // async receive(from: string, message: Message) {
  //   // Customizable Area Start
  //   runEngine.debugLog("Message Recived", message);
  //   if (getName(MessageEnum.SessionResponseMessage) === message.id) {
  //     let token = message.getData(getName(MessageEnum.SessionResponseToken));
  //     this.setState({ token: token });
  //     // this.getListRequest(token);
  //   }
  //   if (
  //     getName(MessageEnum.RestAPIResponceMessage) === message.id &&
  //     this.getProductApiCallId != null &&
  //     this.getProductApiCallId ===
  //       message.getData(getName(MessageEnum.RestAPIResponceDataMessage))
  //   ) {
  //     var responseJson = message.getData(
  //       getName(MessageEnum.RestAPIResponceSuccessMessage)
  //     );
  //     if (responseJson && !responseJson.errors && responseJson.data) {
  //       this.setState({
  //         arrayHolder: responseJson.data,
  //       });
  //       runEngine.debugLog("arrayHolder", this.state.arrayHolder);
  //     } else {
  //       var errorReponse = message.getData(
  //         getName(MessageEnum.RestAPIResponceErrorMessage)
  //       );
  //       this.parseApiCatchErrorResponse(errorReponse);
  //     }
  //   }
  //   // Customizable Area End
  // }

  async receive(from: string, message: Message) {
    // Customizable Area Start
    runEngine.debugLog("Message Recived", message);

    if (getName(MessageEnum.RestAPIResponceMessage) === message.id) {
      var responseJson = message.getData(
        getName(MessageEnum.RestAPIResponceSuccessMessage)
      );
      if (responseJson && !responseJson.errors) {
        const apiRequestCallId = message.getData(
          getName(MessageEnum.RestAPIResponceDataMessage)
        );
        if (apiRequestCallId != null) {
          //console.log(responseJson.data)
          if (apiRequestCallId === this.userProfileApiCallId) {
            console.log(responseJson.data);
            this.setState({
              fullname: responseJson.data.attributes.fullname,
              email: responseJson.data.attributes.email,
              designation: responseJson.data.attributes.designation,
              full_phone_number: responseJson.data.attributes.full_phone_number,
              address: responseJson.data.attributes.address,
              companyName: responseJson.data.attributes.company_name,
              country: responseJson.data.attributes.country,
              user_type: responseJson.data.attributes.user_type,
            });
          }

          if (apiRequestCallId === this.userUpdateProfileApiCallId) {
            console.log(responseJson, "updated response");
            this.setState({
              fullname: responseJson.data.attributes.fullname,
              email: responseJson.data.attributes.email,
              designation: responseJson.data.attributes.designation,
              full_phone_number: responseJson.data.attributes.full_phone_number,
              address: responseJson.data.attributes.address,
              companyName: responseJson.data.attributes.company_name,
              country: responseJson.data.attributes.country,
              user_type: responseJson.data.attributes.user_type,
            });
          }

          if (apiRequestCallId === this.userUpdateChangePassword) {
            console.log(responseJson, "updated password");
          }
        }
      } else {
        var errorReponse = message.getData(
          getName(MessageEnum.RestAPIResponceErrorMessage)
        );
        this.parseApiCatchErrorResponse(errorReponse);
      }
    }
  }

  // Handler Product Listing

  getProductListing = (): boolean => {
    const headers = {
      "Content-Type": configJSON.productApiContentType,
      token: window.localStorage.getItem("auth"),
    };

    const requestMessage = new Message(
      getName(MessageEnum.RestAPIRequestMessage)
    );

    this.getProductListingApiCallId = requestMessage.messageId;
    console.log(
      "this.getProductListingApiCallId",
      this.getProductListingApiCallId
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIResponceEndPointMessage),
      configJSON.listApiEndPoint
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestHeaderMessage),
      JSON.stringify(headers)
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestMethodMessage),
      configJSON.apiMethodTypeGet
    );

    runEngine.sendMessage(requestMessage.id, requestMessage);

    return true;
  };

  getToken = () => {
    const msg: Message = new Message(
      getName(MessageEnum.SessionRequestMessage)
    );
    this.send(msg);
  };

  handleCheckBox = (index: any, value: any) => {
    // console.log(index, value);
    let arr = Object.assign([], this.state.displayProducts, {
      [index]: {
        ...this.state.displayProducts[index],
        checkboxStatus: value,
      },
    });

    // let link;
    // this.state.displayProducts.includes();
    // console.log("link", link);
    this.setState({
      ...this.state,
      displayProducts: arr,
    });
  };

  handleToggle = (index: any, value: any) => {
    let arr = Object.assign([], this.state.displayProducts, {
      [index]: {
        ...this.state.displayProducts[index],
        activeStatus: value,
      },
    });

    this.setState({
      ...this.state,
      displayProducts: arr,
    });
  };

  onMenuToggle = () => {
    this.setState({
      menuCollapsed: !this.state.menuCollapsed,
    });
  };

  handleToggleAddProductModal = () => {
    this.setState({
      ...this.state,
      addNewProductModal: !this.state.addNewProductModal,
    });
  };

  handleToggleAddVariationModal = () => {
    this.setState({
      ...this.state,
      addNewVariationModal: !this.state.addNewVariationModal,
    });
  };

  handleImageUpload = (event: any) => {
    var file = event.target.files[0];
    const reader = new FileReader();
    var url = reader.readAsDataURL(file);

    reader.onloadend = (e: any) => {
      this.setState({
        selectedFile: [reader.result],
      });
    };
    console.log(url);

    this.setState({
      selectedFile: event.target.files[0],
    });
  };

  handleOnDrop = (file: any) => {
    this.setState({
      acceptedFiles: this.state.acceptedFiles.concat(file),
    });
  };
  // Customizable Area Start
  // Customizable Area End
}
