import React from "react";
/**
 * Components
 */
import { withRouter, Link } from "react-router-dom";
import { Formik, Field, Form, FormikHelpers, ErrorMessage } from 'formik';
import RefreshIcon from '@material-ui/icons/Refresh';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import "react-datepicker/dist/react-datepicker.css";
import Switch from "../../../components/src/Switch";
import GenericBackdrop from "../../../components/src/GenericBackdrop";
import Sidebar from "../../../components/src/Sidebar";
/**
 * Controller
 */
import CategoriessubcategoriesController, { Props } from "./CategoriessubcategoriesController.web";
/**
 * Assets
 */
import {
  logo,
  dashboardIcon,
  menuIcon,
  menuDownIcon,
  logsWighBgActiveIcon,
  timeWithBgIcon,
} from "./assets";
import { validationSchema } from './Categoriessubcategories.web.validation';

export const configJSON = require("./config");

interface FormValues {
  roleId: string | null | undefined;
  roleName: string | null | undefined;
  module_roles_attributes: any;
}

class CategoriesSubcategories extends CategoriessubcategoriesController {
  constructor(props: Props) {
    super(props);
    // Customizable Area Start
    // Customizable Area End
  }

  // Customizable Area Start
  // Customizable Area End
  render() {
    return (
      <div className="admin-wrapper">
        {/* Sidebar */}
        <Sidebar menuCollapsed={this.state.menuCollapsed}  />
        <div
          className={`
            ${
              this.state.menuCollapsed
                ? "admin-body-container-collapsed"
                : "admin-body-container"
            } flex`}
        >
          {/* Overview */}
          <div
            className={`w-3/12 bg-white flex-shrink-0 ${
              this.state.menuCollapsed &&
              this.state.userType === configJSON.subscriber
                ? "block"
                : "hidden"
            }`}
          >
            {/* Overview Header */}
            <div className="flex items-center justify-between py-6 px-6 border-b border-light-400 mb-6">
              <div className="flex items-center">
                <img src={menuIcon} alt="" className="h-9" />
                <div className="ml-3">
                  <p className="text-secondary-base text-sm font-bold">
                    Soubscriber-Name
                  </p>
                  <p className="text-primary-light-xs">{this.state.userType}</p>
                </div>
              </div>
              <button className="op-btn-transparent" type="button">
                <img src={menuDownIcon} alt="" className="h-9" />
              </button>
            </div>
            {/* Overview Content */}
            <div className="px-6">
              <div className="pb-5">
                <p className="text-secondary-base text-sm font-bold">
                  Trails Overview
                </p>
                <p className="text-primary-light-xs">
                  Overall members & trails performance
                </p>
              </div>
              <div className="flex items-center justify-between py-3 px-5 bg-transparent-200 mb-2">
                <div>
                  <p className="text-primary-light-xs">Trails logged today</p>
                  <p className="text-secondary-base text-sm font-bold">298</p>
                </div>
                <button className="op-btn-transparent" type="button">
                  <img src={logsWighBgActiveIcon} alt="" className="h-9" />
                </button>
              </div>
              <div className="flex items-center justify-between py-3 px-5 bg-transparent-200 mb-2">
                <div>
                  <p className="text-primary-light-xs">
                    Members logged in today
                  </p>
                  <p className="text-secondary-base text-sm font-bold">20</p>
                </div>
                <button className="op-btn-transparent" type="button">
                  <img src={timeWithBgIcon} alt="" className="h-9" />
                </button>
              </div>
            </div>
          </div>
          {/* Admin Body */}
          <div
            className={
              this.state.menuCollapsed ? "admin-body-collapsed" : "admin-body"
            }
          >
            {/* Page Title */}
            <div className="admin-page-title-wrapper justify-between">
              <div className="flex items-center">
                <button
                  type="button"
                  className="op-btn-primary-transparent"
                  onClick={this.onMenuToggle}
                >
                  <img src={menuIcon} alt="Menu" className="h-9" />
                </button>
                <p className="admin-page-title">{configJSON.title}</p>
              </div>
            </div>
            {/* Page Content */}
            <div className="admin-body-content px-8 py-12">
              <Formik 
                initialValues={{ 
                  roleId: this.state.roleDetailsData?.attributes?.id || '', 
                  roleName: this.state.roleDetailsData?.attributes?.name || '', 
                  module_roles_attributes: [...configJSON.permissionsData] 
                }}
                validationSchema={validationSchema}
                onSubmit={(
                  values: FormValues,
                  { setSubmitting }: FormikHelpers<FormValues>
                ) => {
                  setTimeout(() => {
                    // alert(JSON.stringify(values, null, 2));
                    console.log(values);
                    this.createRole(values);
                    setSubmitting(false);
                  }, 500);
                }}
                enableReinitialize
                render={({ values, setFieldValue, isSubmitting }) => {
                  // console.log(values);
                  return (
                    <Form>
                      <GenericBackdrop open={this.state.loader} />
                      <Field id="roleId" name="roleId" type="hidden" />
                      <div className="grid grid-cols-2 gap-12 pb-8">
                        <div>
                          <div>
                            <div />
                            <div>
                              <div className="field-wrapper">
                                <label htmlFor="categoryName">Category name</label>
                                <Field id="categoryName" name="categoryName" placeholder="Start typing" />
                                {/* <img src={filled} alt="" /> */}
                                <span className="error">
                                  <ErrorMessage name="categoryName" />
                                </span>
                              </div>
                              <div className="field-wrapper">
                                <label htmlFor="parentCategory">Parent Category</label>
                                <Field id="parentCategory" name="parentCategory" placeholder="Start typing" />
                                {/* <img src={filled} alt="" /> */}
                                <span className="error">
                                  <ErrorMessage name="parentCategory" />
                                </span>
                              </div>
                            </div>
                          </div>
                          <div className="field-wrapper">
                            <label htmlFor="discount">Discount</label>
                            <Field id="discount" name="discount" placeholder="" />
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="discount" />
                            </span>
                          </div>
                          <div className="field-wrapper">
                            <label htmlFor="updateDiscountSubcategories">Update discount in subcategories too</label>
                            <div>
                              <Switch 
                                name="updateDiscountSubcategories" 
                                onChange={(e: any): void => { 
                                  console.log(e.target.checked); 
                                  setFieldValue('updateDiscountSubcategories', e.target.checked);
                                }} 
                              />
                            </div>
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="updateDiscountSubcategories" />
                            </span>
                          </div>
                        </div>
                        <div>
                          <div className="field-wrapper">
                            <label htmlFor="description">Description</label>
                            <Field id="description" name="description" placeholder="Start typing" />
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="description" />
                            </span>
                          </div>
                          <div className="flex">
                            <div className="field-wrapper mr-8">
                              <label htmlFor="isActive">Is Active?</label>
                              <div>
                                <Switch 
                                  name="isActive" 
                                  onChange={(e: any): void => { 
                                    console.log(e.target.checked); 
                                    setFieldValue('isActive', e.target.checked);
                                  }} 
                                />
                              </div>
                              {/* <img src={filled} alt="" /> */}
                              <span className="error">
                                <ErrorMessage name="isActive" />
                              </span>
                            </div>
                            <div className="field-wrapper mr-8">
                              <label htmlFor="makePrivate">Make Private?</label>
                              <div>
                                <Switch 
                                  name="makePrivate" 
                                  onChange={(e: any): void => { 
                                    console.log(e.target.checked); 
                                    setFieldValue('makePrivate', e.target.checked);
                                  }} 
                                />
                              </div>
                              {/* <img src={filled} alt="" /> */}
                              <span className="error">
                                <ErrorMessage name="makePrivate" />
                              </span>
                            </div>
                            <div className="field-wrapper">
                              <label htmlFor="defaultCategory">Make Default Category</label>
                              <div>
                                <Switch 
                                  name="defaultCategory" 
                                  onChange={(e: any): void => { 
                                    console.log(e.target.checked); 
                                    setFieldValue('defaultCategory', e.target.checked);
                                  }} 
                                />
                              </div>
                              {/* <img src={filled} alt="" /> */}
                              <span className="error">
                                <ErrorMessage name="defaultCategory" />
                              </span>
                            </div>
                          </div>
                        </div>
                      </div>
                      <div className="grid grid-cols-2 gap-12 border-t border-light-300 py-8">
                        <div>
                          <div className="field-wrapper">
                            <label htmlFor="metaTitle">Meta Title</label>
                            <Field id="metaTitle" name="metaTitle" placeholder="Type meta title. This will be added to the HTML" />
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="metaTitle" />
                            </span>
                          </div>
                          <div className="field-wrapper">
                            <label htmlFor="metaDescription">Meta Description</label>
                            <Field id="metaDescription" name="metaDescription" placeholder="Type meta description. This will be added to the HTML" />
                            {/* <img src={filled} alt="" /> */}
                            <span className="error">
                              <ErrorMessage name="metaDescription" />
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center justify-between mt-12">
                        <button type="button" className="button op-btn-light">
                          Cancel
                        </button>
                        <button type="submit" className="button op-btn-primary flex items-center"  disabled={isSubmitting}>
                          {values.roleId ? 'Update Role' : 'Create Role'}
                          {isSubmitting && (
                            <RefreshIcon style={{ fontSize: 16, marginLeft: 10 }} className="op-rotate" />
                          )}
                        </button>
                      </div>
                    </Form>
                  )
                }} 
              />
            </div>
          </div>
        </div>
        {/* Snackbar for display success and failed messages. */}
        <Snackbar open={this.state.snackBar.show} autoHideDuration={3000} onClose={this.closeSnackBarHandler}>
          <MuiAlert elevation={6} variant="filled" onClose={this.closeSnackBarHandler} severity={this.state.snackBar.type}>
            {this.state.snackBar.message}
          </MuiAlert>
        </Snackbar>
      </div>
    );
  }
}

//@ts-ignore
export default withRouter(CategoriesSubcategories);
