import { IBlock } from "../../../framework/src/IBlock";
import { Message } from "../../../framework/src/Message";
import { BlockComponent } from "../../../framework/src/BlockComponent";
import MessageEnum, {
  getName,
} from "../../../framework/src/Messages/MessageEnum";
import { runEngine } from "../../../framework/src/RunEngine";
import {identity, isEmpty} from 'lodash'

// Customizable Area Start
// Customizable Area End

export const configJSON = require("./config");

export interface Props {
  navigation: any;
  id: string;
  // Customizable Area Start
  // Customizable Area End
}

interface S {
  // Customizable Area Start
  email: string;
  errorEmail: string;
  password: string;
  errorPassword: string;
  confirmPassword: string;
  errorConfirmPassword: string;
  // Customizable Area End
}

interface SS {
  // Customizable Area Start
  id: any;
  // Customizable Area End
}

export default class EmailAccountLoginController extends BlockComponent<
  Props,
  S,
  SS
> {
  // Customizable Area Start
  apiEmailLoginCallId: string = "";
  validationApiCallId: string = "";
  apiPasswordCallId: string = "";
  apiForgotCallId: string = "";

  emailReg: RegExp;
  labelTitle: string = "";
  // Customizable Area End

  constructor(props: Props) {
    super(props);
    this.receive = this.receive.bind(this);

    // Customizable Area Start
    this.subScribedMessages = [
      getName(MessageEnum.CountryCodeMessage),
      getName(MessageEnum.RestAPIResponceMessage),
      getName(MessageEnum.ReciveUserCredentials),
    ];

    this.state = {
      email: '',
      errorEmail:'',
      password: "",
      errorPassword: "",
      confirmPassword:'',
      errorConfirmPassword:'',
    };

    this.emailReg = new RegExp("");
    // Customizable Area End

    runEngine.attachBuildingBlock(this as IBlock, this.subScribedMessages);
  }

  async componentDidMount() {
    // this.callGetValidationApi();
    this.send(new Message(getName(MessageEnum.RequestUserCredentials)));
    // Customizable Area Start

    // Customizable Area End
  }
  getToken = () => {
    const msg: Message = new Message(
      getName(MessageEnum.SessionRequestMessage)
    );
    this.send(msg);
   console.log('msg',msg)

  };

  // Customizable Area Start

  txtInputPasswordWebProps = {
    onChange: (text: any) => {
      this.setState({ errorPassword:'' , password: text?.target.value });
    },
  };

  txtInputConfirmPasswordWebProps = {
    onChange: (text: any) => {
      this.setState({ errorConfirmPassword:'' , confirmPassword: text?.target.value });
    },
  };


  txtInputEmailWebProps = {
    onChange: (text: any) => {
      this.setState({ errorEmail:''  ,email: text?.target.value });
      //@ts-ignore
      this.txtInputEmailProps.value = text?.target.value;
    },
  };

  txtInputEmailMobileProps = {
    ...this.txtInputEmailWebProps,
    autoCompleteType: "email",
    keyboardType: "email-address",
  };

  txtInputEmailProps = this.isPlatformWeb()
    ? this.txtInputEmailWebProps
    : this.txtInputEmailMobileProps;




  async receive(from: string, message: Message) {
    // Customizable Area Start
    if (getName(MessageEnum.RestAPIResponceMessage) === message.id) {

      const apiRequestCallId = message.getData(
        getName(MessageEnum.RestAPIResponceDataMessage)
      );

      var responseJson = message.getData(
        getName(MessageEnum.RestAPIResponceSuccessMessage)
      );

      var errorReponse = message.getData(
        getName(MessageEnum.RestAPIResponceErrorMessage)
      );
      if (apiRequestCallId) {
        if (apiRequestCallId === this.apiPasswordCallId) {
          if (responseJson.data ) {
             console.log('responseJson',responseJson)
             // @ts-ignore
             alert('Password Successfully set')
          }else{
            alert('Oops,something went wrong ?')
          }
        }
        else if (apiRequestCallId === this.apiForgotCallId) {
              console.log('responseJson',responseJson)
          if (!responseJson?.errors[0].otp ) {
            localStorage.setItem('email',this.state.email)
             console.log('responseJson',responseJson)
             // @ts-ignore
             this.props.history.push("new-password")
          }else{
            alert('Account not found')
          }
        }
      }
     }
    }
    //apiForgotCallId

  sendLoginFailMessage() {
    const msg: Message = new Message(getName(MessageEnum.LoginFaliureMessage));
    this.send(msg);
  }

//   sendLoginSuccessMessage() {
//     const msg: Message = new Message(getName(MessageEnum.LoginSuccessMessage));

//     msg.addData(getName(MessageEnum.LoginUserName), this.state.email);
//     msg.addData(getName(MessageEnum.CountyCodeDataMessage), null);
//     msg.addData(getName(MessageEnum.LoginPassword), this.state.password);
//     msg.addData(
//       getName(MessageEnum.LoginIsRememberMe),
//       this.state.checkedRememberMe
//     );

//     this.send(msg);
//   }

  saveLoggedInUserData(responseJson: any) {
    console.log('responseJsonresponseJson',responseJson)
    if (responseJson && responseJson.meta && responseJson.meta.token) {
      const msg: Message = new Message(getName(MessageEnum.SessionSaveMessage));

      msg.addData(
        getName(MessageEnum.SessionResponseData),
        JSON.stringify(responseJson)
      );
      msg.addData(
        getName(MessageEnum.SessionResponseToken),
        responseJson.meta.token
      );
       localStorage.setItem('auth',JSON.stringify(responseJson.meta.token))

      this.send(msg);


    }
  }

  openInfoPage() {
    // const msg: Message = new Message(getName(MessageEnum.AccoutLoginSuccess));
    // console.log('msg',msg)
    // console.log('this.props',this.props)
    // msg.addData(getName(MessageEnum.NavigationPropsMessage), this.props);

    // this.send(msg);

    // localStorage.setItem('auth',);
    alert('Login Successful')
    // this.props.history.push("select-domain")
  }

  goToForgotPassword() {
    const msg: Message = new Message(
      getName(MessageEnum.NavigationForgotPasswordMessage)
    );
    msg.addData(getName(MessageEnum.NavigationPropsMessage), this.props);
    msg.addData(getName(MessageEnum.NavigationForgotPasswordPageInfo), "email");
    this.send(msg);
  }

  goToSocialLogin() {
    const msg: Message = new Message(
      getName(MessageEnum.NavigationSocialLogInMessage)
    );
    msg.addData(getName(MessageEnum.NavigationPropsMessage), this.props);
    this.send(msg);
  }

  createNewPassword(): Boolean {
    // console.log("API HIT");
    // console.log('this.state',this.state)

    if (isEmpty(this.state.password)) {
       this.setState({
          errorPassword:configJSON.errorPasswordEmpty
      })
      return false;
    }else if(this.state.password.length>10){
        this.setState({
                errorPassword:configJSON.errorPasswordLengthMax
            })
      return false;
    }else if (this.state.password.length < 8){
        this.setState({
                errorPassword:configJSON.errorPasswordLengthLess
            })
      return false;
    }

     if (isEmpty(this.state.confirmPassword)) {
       this.setState({
            errorConfirmPassword:configJSON.errorPasswordEmpty
      })
      return false;
    }else if( this.state.password !== this.state.confirmPassword){
        this.setState({
            errorConfirmPassword:configJSON.errorConfirmPasswordNotMatch
      })
      return false;
    }

    const header = {
      "Content-Type": configJSON.forgotPasswordAPiContentType,
    };

    console.log('this.state',this.state)
    const attrs = {
      password: this.state.password,
      confirm_password: this.state.confirmPassword,
      email: this.state.email || localStorage.getItem('email') || '',
    };

    const data = {
      type: "email_account",
      attributes: attrs,
    };

    const httpBody = {
      data: data,
    };

    console.log('httpBody',httpBody)

    const requestMessage = new Message(
      getName(MessageEnum.RestAPIRequestMessage) 
    );

    // console.log("requestMessage", requestMessage);
    this.apiPasswordCallId = requestMessage.messageId;
    requestMessage.addData(
      getName(MessageEnum.RestAPIResponceEndPointMessage),
      configJSON.newPasswordApiEndPoint
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestHeaderMessage),
      JSON.stringify(header)
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestBodyMessage),
      JSON.stringify(httpBody)
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestMethodMessage),
      configJSON.httpPostMethod
    );


    runEngine.sendMessage(requestMessage.id, requestMessage);

    return true;
  }

  callGetValidationApi() {
    const headers = {
      "Content-Type": configJSON.validationApiContentType,
    };

    const getValidationsMsg = new Message(
      getName(MessageEnum.RestAPIRequestMessage)
    );
    this.validationApiCallId = getValidationsMsg.messageId;

    getValidationsMsg.addData(
      getName(MessageEnum.RestAPIResponceEndPointMessage),
      configJSON.urlGetValidations
    );

    getValidationsMsg.addData(
      getName(MessageEnum.RestAPIRequestHeaderMessage),
      JSON.stringify(headers)
    );
    getValidationsMsg.addData(
      getName(MessageEnum.RestAPIRequestMethodMessage),
      configJSON.validationApiMethodType
    );
    runEngine.sendMessage(getValidationsMsg.id, getValidationsMsg);
  }


  handleForgotPassword() : Boolean {
      if (
        this.state.email === null ||
        this.state.email.length === 0 ||
        !this.emailReg.test(this.state.email)
      ) {
        this.setState({
            errorEmail :configJSON.pleaseEnterAValidEmail
        })  
        return false;
      }

    const header = {
      "Content-Type": configJSON.forgotPasswordAPiContentType,
    };

    console.log('this.state',this.state)
    const attrs = {
      email: this.state.email
    };

    const data = {
      type: "email_account",
      attributes: attrs,
    };

    const httpBody = {
      data: data,
    };

    console.log('httpBody',httpBody)

    const requestMessage = new Message(
      getName(MessageEnum.RestAPIRequestMessage) 
    );

    // console.log("requestMessage", requestMessage);
    this.apiForgotCallId = requestMessage.messageId;
    requestMessage.addData(
      getName(MessageEnum.RestAPIResponceEndPointMessage),
      configJSON.ForgotPasswordApiEndPoint
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestHeaderMessage),
      JSON.stringify(header)
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestBodyMessage),
      JSON.stringify(httpBody)
    );

    requestMessage.addData(
      getName(MessageEnum.RestAPIRequestMethodMessage),
      configJSON.httpPostMethod
    );


    runEngine.sendMessage(requestMessage.id, requestMessage);

      return true;
  }
}
